contracts/ERC721/ERC721Basic.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract ERC721Basic is ERC721, AccessControl {

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor(string memory name, string memory symbol)
    ERC721(name, symbol)
    {
        _grantRole(MINTER_ROLE, _msgSender());
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function mint(address account, uint256 tokenId) external onlyRole(MINTER_ROLE)
    {
        _safeMint(account, tokenId);
    }
}

contracts/ERC721/SmartWallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/interfaces/IERC1271.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract SmartWallet is Ownable, IERC1271 {
    constructor(address initialOwner) Ownable(initialOwner) {
    }

    function execute(address target, uint256 value, bytes memory data) external onlyOwner() returns (bytes memory) {
        (bool success, bytes memory returndata) = target.call{ value: value }(data);
        require(success);
        return returndata;
    }

    function isValidSignature(bytes32 hash, bytes memory signature) external view override returns (bytes4 magicValue) {
        require(owner() == ECDSA.recover(hash, signature));
        return this.isValidSignature.selector;
    }
}

